(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["commonProblem"],{8451:function(t,n,e){},9865:function(t,n,e){"use strict";e.r(n);var i=function(){var t=this,n=t.$createElement,e=t._self._c||n;return e("div",{staticClass:"commonProblem"},t._l(t.problemList,function(n,i){return e("div",{key:i},[e("div",[e("p",[t._v(t._s(n.id))]),e("p",[t._v(t._s(n.title))])]),t._l(n.children,function(n,i){return e("div",{key:i},[e("div",[t._v(t._s(n.title))]),e("div",[t._v(t._s(n.content))]),n.content2?e("div",[t._v(t._s(n.content2))]):t._e(),n.content3?e("div",[t._v(t._s(n.content3))]):t._e()])})],2)}))},o=[],c=[{id:"01",title:"实名认证",children:[{title:"问：如何进行实名认证？",content:"进入支付宝搜索生活号“万物互联”，点击关注后进入生活号。点击“查询续费”输入或扫描ICCID号，进入实名认证系统。输入IMEI号、姓名、身份证和手机号码进行实名认证，输入验证码后，将自动跳转到手机淘宝进行人脸和身份证拍照进行实名认证，确认无误之后点击提交进行实名审核。"},{title:"问：一个身份证能绑定几张卡？",content:"一个身份证能绑定5张卡，用爸爸或者妈妈的身份证进行绑定。"},{title:"问：卡我不想用了，能现在注销吗？",content:"在卡停机30天后将自动注销，不能手动注销。"},{title:"问：我注册后进行实名认证了，个人资料不会被泄漏吧？",content:"不会的，实名认证后的个人资料都是进行保密处理的，我们是看不到您的信息的。"}]},{id:"02",title:"充值续费",children:[{title:"问：套餐如何使用？",content:"目前我们有单流量套餐和话费加流量套餐，卡在没有话费只有流量的情况下是只能使用网络，不能接打电话。必须卡要存在有话费，才可以接电话。套餐全国通用，0漫游费，0月租，接听免费，所有套餐都是6个月的有效期，购买套餐之后在六个月之内使用完即可。"},{title:"问：输入的IMEI号显示不正确？",content:"确认您输入的是15位数字的IMEI号，如果确认IMEI使用不了，请将号码发送给我们的客服查询。（注：手机号码那栏输入家长的手机号码进行验证码接收绑定）"},{title:"问：我可以在其他地方充值话费吗？",content:"不可以的 ，我们目前不支持其他地方充值，在其他地方充值是不可以用的，您直接可以在我们平台上直接进行充值的。"},{title:"问：我想查询话费和流量详情，该在哪查询呢？",content:"进入支付宝搜索生活号“万物互联”，点击关注后进入生活号。点击“查询续费”即可查询流量使用详情，会有详细的流量以及话费记录。"},{title:"问：我在淘宝“我的设备”里面看到我的卡流量话费都用完了，但是我没有用呀，这是怎么回事？",content:"请去我们生活号里面查询，淘宝里面是不显示套餐这些的。"},{title:"问：你们这个卡是哪的，我能在联通公司查询到吗，我可以在联通公司去充值续费吗？",content:"我们的卡是属于阿里巴巴的联通物联卡，这个是可以联通查询的，但不可以在联通那边续费的，需要去到我们“万物互联”平台进行充值续费的哦！"}]},{id:"03",title:"使用操作",children:[{title:"问：卡显示未激活／机卡分离停机／已停机",content:"1.实名认证完成之后物联网卡是处于一个可激活状态（生活号显示未激活），把卡插入设备开机会自动变成已激活，一般这个过程在5分钟左右状态会变更好，随后可以开始正常使用。  ",content2:"2.机卡分离，若把卡换到其他设备使用，就会显示“机卡分离停机”，点击旁边激活按钮，就会显示已激活，仅显示机卡分离是不影响物联网卡的使用。（此方法用于卡没有插在绑定的IMEI号设备上使用）",content3:"3.停机，请确认您的话费是否用完，或刚充值完，状态没有及时更新。"},{title:"问：扫描我们手表卡上面的ICCID号显示不正确，我的外面固定卡的卡板丢了，看不到ICCID号怎么办？",content:"若出现扫描不正确状况，请试着手动输入，固定卡板丢失也不要紧，我们小卡上面也有ICCID号，号码为19位8986开头的数字。"},{title:"问：这个卡我插入设备没有信号？",content:"您可以换手机试试是否有信号，如果还是没有信号的话可在卡上面垫张纸试试，多测试两台设备。如果还是没有信号，请联系我们的客服，将卡寄回测试。"},{title:"问：我把卡插入手表没有信号，插入手机有信号，这个是怎么回事？",content:"请确认卡是否插入正确，手表是否支持。"},{title:"问：手表ANP 可以设置吗？后台卡的状态如何？",content:"APN需要设置成unim2m.njm2mapn"},{title:"问：这个卡归属地是哪，我可以选择地区吗？外地卡不会有漫游费吗？",content:"这个卡的归属地在南京，不可以选择地区的，卡是没有漫游费或月租等其他任何费用的哦！"},{title:"问：我卡丢了，能补办吗？",content:"可以补办的，只需将旧卡的ICCID号和补卡邮寄地址和联系人提供给客服，便可为您补发一张新卡。（新卡号随机，旧卡的套餐将会转移到新卡中）"},{title:"问：我在你们平台可以开票吗？",content:"可以的 ，请提供公司开票资料，需要加3个点的税点。"}]}],l=c,r={name:"home",data:function(){return{problemList:l}},components:{},created:function(){},methods:{}},s=r,d=(e("e597"),e("2877")),u=Object(d["a"])(s,i,o,!1,null,null,null);u.options.__file="commonProblem.vue";n["default"]=u.exports},e597:function(t,n,e){"use strict";var i=e("8451"),o=e.n(i);o.a}}]);
